<script>

    function loadContent(url, div)
    {
        if(div === '')
            div='main-wrapper';

        $('#'+div).empty();

        let target = document.getElementById('main-wrapper');
        let spinner = new Spinner(opts).spin(target);

        $.ajax({
            type: "POST",
            dataType: "html",
            url: '<?php echo base_url();?>' + url
        }).done(function(response, textStatus, jqXHR)
        {
            if(response != 'NO_LOGGED')
            {
                $(document).find('#'+div).html(response);
                $('#pathBreadcrumb').empty();
            }
            else if(response == 'NO_LOGGED')
            {
                toastr.error("You don\'t have access.");
                window.location.replace('<?php echo base_url();?>');
            }

            spinner.stop();
        }).fail(function(jqHTR, textStatus, thrown)
        {
            toastr.error('Something is wrong with AJAX:' + textStatus);
            spinner.stop();

        });
    }

    function doSomething(url, data)
    {
        let target = document.getElementById('main-wrapper');
        let spinner = new Spinner(opts).spin(target);

        $.ajax({
            type: "POST",
            dataType: "html",
            url: '<?php echo base_url();?>' + url,
            data: {data:data}
        }).done(function(response, textStatus, jqXHR)
        {
            if(response == 'NO_LOGGED')
            {
                toastr.error("You don\'t have access.");
                window.location.replace('<?php echo base_url();?>');
            }
            else if(response == 'SENT')
            {
                toastr.success("An email was sent.");
            }

            spinner.stop();
        }).fail(function(jqHTR, textStatus, thrown)
        {
            toastr.error('Something is wrong with AJAX:' + textStatus);
            spinner.stop();
        });
    }

    $('body').on('click', '.show_modal', function ()
    {
        let detail=jQuery(this).children('.hide').html();//alert(detail);
        let title=jQuery(this).children('.hide').attr('data-title');//alert(title);
        let modal = $('#modal_div');
        modal.find('.modal-title').html(title);
        modal.find('#modal_body').html(detail);
        modal.appendTo("body").modal('show');
    }).on('change', '.selectTwo', function () {
        this.setAttribute('value', this.value);
    });
    
    function isValid(frm = '')
    {
        let validated = 0;
        let forms;
        //console.log(frm);
        if(frm === '')
            forms = document.getElementsByClassName('needValidation');
        else
            forms = $('#'+frm);

        let validation = Array.prototype.filter.call(forms, function(form)
        {//console.log(form);
            form.classList.add('wasValidated');
            validated = form.checkValidity();
        });

        return validated
    }

    function isValidModal(modal)
    {
        let validated = 0;
        let forms = modal.find('.needValidationModal');
        let validation = Array.prototype.filter.call(forms, function(form)
        {
            //console.log(form);
            form.classList.add('wasValidated');
            validated = form.checkValidity();
        });

        return validated
    }

