    function refreshProjectsTable(isRefresh = false)
    {
        let spinner ='';
        if(!isRefresh)
        {
            let target = document.getElementById('divContent');
            spinner = new Spinner(opts).spin(target);
        }

        $.ajax({
            url: '<?php echo base_url("Main/getData");?>',
            type: 'POST',
            data: {data_type:'list_project',view_url:'project/DataTableListProject'}
        }).done(function(response, textStatus, jqXHR)
        {
            if(response !== 'NO_LOGGED')
            {
                $('#data_table_project').html(response);
                projectsUsers();
                if(!isRefresh)
                    dtProject();
            }
            else if(response === 'NO_LOGGED')
            {
                toastr.error("You don\'t have access.");
                window.location.replace('<?php echo base_url()?>');
            }

            if(!isRefresh)
                spinner.stop();
        });
    }

    function projectsUsers(isRefresh = false)
    {
        $('.fa-users').off('click').on('click', function (e) {
            var idProject = $(this).attr('projectID');
            getUsers(idProject);
        });

        function getUsers(idProject)
        {
            $('#users').empty();
            let spinner = '';

            if(!isRefresh)
            {
                let target = document.getElementById('divContent');
                spinner = new Spinner(opts).spin(target);
            }

            $.ajax({
                url: '<?php echo base_url("Main/getData");?>',
                type: 'POST',
                data: {data_type: 'upd_project_user', view_url: 'project/userProject', id_project: idProject}
            }).done(function (response, textStatus, jqXHR) {
                if (response !== 'NO_LOGGED') {
                    $('#users').html(response);
                }
                else if (response === 'NO_LOGGED') {
                    toastr.error("You don\'t have access.");
                    window.location.replace('<?php echo base_url()?>');
                }

                if(!isRefresh)
                    spinner.stop();
            });
        }
    }

